        try {
            JSONObject jsonObject = new JSONObject(s);

            JSONArray category_array=jsonObject.getJSONArray("Category");

            if(Globas.categories.size()>0){
                Globas.categories.clear();
            }

            for(int i=0;i<category_array.length();i++)
            {
                JSONObject temp=(JSONObject) category_array.get(i);
                Globas.categories.add(new Category(temp.getString("id"),temp.getString("title")));

            }

            JSONArray collection_array=jsonObject.getJSONArray("Collection");

            if(Globas.dhadhas.size()>0)
            {
                Globas.dhadhas.clear();
            }
            for(int i=0;i<collection_array.length();i++){

                JSONObject temp=(JSONObject)collection_array.get(i);
                Globas.dhadhas.add(new Dhadha(temp.getString("id"),temp.getString("category_id"),temp.getString("descr"),temp.getString("answer")));

            }

            Log.e("MainActivity","category: "+category_array.length()+" collection: "+collection_array.length());

        }catch (JSONException e)
        {
            Log.e("MainActivity","Json exception: "+e);
        }


        getSupportFragmentManager().beginTransaction().add(R.id.main_content,CategoryFragment.newInstance(1)).commit();

